mapper.insert:
INSERT INTO t_customer_return_list
(
return_number, return_date, amount_paid, amount_payable,
remarks, state, customer_id, user_id
)
VALUES
(
#{returnNumber}, #{returnDate}, #{amountPaid}, #{amountPayable},
#{remarks}, #{state}, #{customerId}, #{userId}
)

INSERT INTO t_customer_return_list_goods
(
goods_id, goods_code, goods_name, goods_model, goods_unit,
goods_num, price, total, customer_return_list_id, goods_type_id
)
VALUES
(
#{goodsId}, #{goodsCode}, #{goodsName}, #{goodsModel}, #{goodsUnit},
#{goodsNum}, #{price}, #{total}, #{customerReturnListId}, #{goodsTypeId}
)

mapper.select:
SELECT t_customer_return_list.customer_return_list_id, t_customer_return_list.return_number, t_customer_return_list.return_date, t_customer_return_list.amount_paid, t_customer_return_list.amount_payable, t_customer_return_list.state, t_customer_return_list.customer_id, t_customer_return_list.user_id, t_customer_return_list.remarks, t_user.true_name, t_customer.customer_name FROM t_customer_return_list LEFT JOIN t_user ON t_customer_return_list.user_id = t_user.user_id LEFT JOIN t_customer ON t_customer_return_list.customer_id = t_customer.customer_id
AND t_customer_return_list.return_number LIKE CONCAT('%', #{returnNumber} ,'%')
AND t_customer_return_list.customer_id = #{customerId}
AND t_customer_return_list.state = #{state}
AND t_customer_return_list.return_date >= #{sTime}
AND t_customer_return_list.return_date <= #{eTime}

mapper.update:
UPDATE t_customer_return_list
SET state = 1
WHERE customer_return_list_id = #{customerReturnListId}
